    function debounce(func, wait) {
      let timeout;
      return function() {
        const context = this;
        const args = arguments;
        clearTimeout(timeout);
        timeout = setTimeout(() => func.apply(context, args), wait);
      };
    }

    // Example usage:
    const inputHandler = debounce((event) => {
      console.log(`Input value: ${event.target.value}`);
    }, 300);

